import{_ as e,c as a,o as t,a as r}from"./app.fb7b9bec.js";const h=JSON.parse('{"title":"Yaml 学习","description":"","frontmatter":{},"headers":[{"level":2,"title":"相关链接","slug":"相关链接","link":"#相关链接","children":[]}],"relativePath":"index.md","lastUpdated":1672974235000}'),l={name:"index.md"},o=r('<h1 id="yaml-学习" tabindex="-1">Yaml 学习 <a class="header-anchor" href="#yaml-学习" aria-hidden="true">#</a></h1><p><strong>Yaml</strong> <code>/ˈjæməl/</code> （Yaml Ain&#39;t a Markup Language，即 Yaml 不是一种标记语言）是一种高可读的数据序列化的语言，可以被大多数的编程语言支持使用，主要用于数据序列化、配置文件。</p><p>常见的标记语言有：XML、HTML语言等。</p><p>它最初代表 &quot;Yet Another Markup Language&quot;，但后来更改为 &quot;Yaml Ain&#39;t Makeup Language&quot; 以区别于真正的标记语言。</p><p>数据序列化，就是可以高效的标识或描述数据及数据关系的，以便于存储和传输。</p><p><strong>优点</strong>：</p><ul><li>语法简单</li><li>结构清晰，易于阅读</li><li>功能丰富，可以描述比JSON更加复杂的结构</li></ul><p><strong>缺点</strong>：</p><ul><li>使用缩进表示层级关系，而缩进使用的是空格，所以对层级关系要求严格，层级错误就会导致数据错误</li><li>键和值在 <code>:</code> 之间使用 <code>:</code> 空格隔开，如果键和值在同一行编写时，必须提供空格</li></ul><h2 id="相关链接" tabindex="-1">相关链接 <a class="header-anchor" href="#相关链接" aria-hidden="true">#</a></h2><ul><li><a href="https://onlineyamltools.com/convert-yaml-to-json" target="_blank" rel="noreferrer">yaml to json converter</a></li><li><a href="https://jsonformatter.org/yaml-validator" target="_blank" rel="noreferrer">yaml validator</a></li></ul>',11),n=[o];function i(s,d,c,p,_,m){return t(),a("div",null,n)}const g=e(l,[["render",i]]);export{h as __pageData,g as default};
